let connection=null;setupConnection=async function(){connection=await(new signalR.HubConnectionBuilder).withUrl("/notificationshub").build();await connection.on("RecieveNewNotificationMessage",n=>{var t,i;if(n.length!=0)for(t in n)i=document.getElementById("alert"),i.innerHTML+=`<div class="alert alert-success alert-dismissible fade show" role="alert">
                <p>${n[t]}<p/>
                <button type="button" class="close" data-dismiss="alert" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>`,document.appendChild("div")});await connection.start().catch(n=>console.error(n.toString())).then(()=>{connection.invoke("CheckForNewNotifications")})};setupConnection();